import "./App.css";
import * as React from "react";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import axios, * as others from "axios";
import Avatar from "@mui/material/Avatar";
import CssBaseline from "@mui/material/CssBaseline";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import { BarChart } from "@mui/x-charts/BarChart";
import DynamicTable from "./Table";

function App() {
  const [selectedVendor, setSelectedVendor] = React.useState("");
  const [optionsVendor, setOptionsVendor] = React.useState([]);
  const [selectedProduct, setSelectedProduct] = React.useState("");
  const [optionsProduct, setOptionsProduct] = React.useState([]);

  React.useEffect(() => {
    const fetchVendors = async () => {
      axios.get("/api/vendors").then(
        (response) => {
          console.log(response);
          if (response.status === 200) {
            setOptionsVendor(response.data);
            console.log(response.data);
          } else {
            console.log(`Status code ${response.status}`);
          }
        },
        (error) => {
          console.log(error);
        }
      );
    };
    fetchVendors();
  }, []);
  const fetchProducts = async (data) => {
    console.log(data);
    axios.get(`/api/products/${data}`).then(
      (response) => {
        console.log(response);
        if (response.status === 200) {
          setOptionsProduct(response.data);
        } else {
          console.log(`Status code ${response.status}`);
        }
      },
      (error) => {
        console.log(error);
      }
    );
  };

  return (
    <Grid container component="main" sx={{ height: "100vh" }}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={7}>
        <BarChart
          xAxis={[
            {
              id: "barCategories",
              data: optionsProduct.map((item) => item.name).slice(0, 4),
              scaleType: "band",
            },
          ]}
          series={[
            {
              data: [2, 5, 3],
            },
          ]}
          width={800}
          height={300}
        />
        <DynamicTable vendor={selectedVendor} />
      </Grid>
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <Box
          sx={{
            my: 8,
            mx: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}></Avatar>
          <Box component="form" sx={{ mt: 3 }}>
            <Autocomplete
              disablePortal
              id="combo-box-demo"
              options={optionsVendor.map((item) => item.name)}
              sx={{ width: 300, padding: 3 }}
              renderInput={(params) => (
                <TextField {...params} label="Vendor name" />
              )}
              onChange={(event, value) => {
                console.log(value);
                setSelectedVendor(value);
                fetchProducts(value);
              }}
            />
          </Box>
          <Box component="form" sx={{ mt: 20 }}>
            <Autocomplete
              disablePortal
              id="combo-box-demo"
              disabled={optionsProduct.length === 0}
              options={optionsProduct.map((item) => item.name)}
              sx={{ width: 300, padding: 3 }}
              renderInput={(params) => (
                <TextField {...params} label="Product name" />
              )}
              onChange={(event, value) => {
                console.log(value);
                setSelectedProduct(value);
              }}
            />
          </Box>
        </Box>
      </Grid>
    </Grid>
  );
}

const months = {
  January: 1,
  February: 2,
  March: 3,
  April: 4,
  May: 5,
  June: 6,
  July: 7,
  August: 8,
  September: 9,
  October: 10,
  November: 11,
  December: 12,
};

export default App;
